--- === cp.apple.finalcutpro.prefs.GeneralPanel ===
---
--- General Panel Module.

local require = require

local log				    = require "hs.logger" .new "GeneralPanel"

local tools                 = require "cp.tools"

local axutils               = require "cp.ui.axutils"
local Button                = require "cp.ui.Button"
local PopUpButton           = require "cp.ui.PopUpButton"
local Panel                 = require "cp.apple.finalcutpro.prefs.Panel"

local childFromTop          = axutils.childFromTop

local GeneralPanel = Panel:subclass("cp.apple.finalcutpro.prefs.GeneralPanel")

--- cp.apple.finalcutpro.prefs.GeneralPanel.TIME_DISPLAY -> table
--- Constant
--- The time display options.
GeneralPanel.static.TIME_DISPLAY = {
    ["HH:MM:SS:FF Subframes"] = "",
    ["HH:MM:SS:FF"] = "",
    ["Frames"] = "",
    ["Seconds"] = ""
}

--- cp.apple.finalcutpro.prefs.GeneralPanel.timeDisplay([value]) -> string | nil
--- Function
--- Gets to sets the Time Display value.
---
--- Parameters:
---  * value - An optional value to set the Time Display.
---
--- Returns:
---  * The time display if successful, otherwise `nil` if an error occurs.
function GeneralPanel.static.timeDisplay(value)
    if value then
        --------------------------------------------------------------------------------
        -- Setter:
        --------------------------------------------------------------------------------
        if tools.tableContains(GeneralPanel.mt.TIME_DISPLAY, value) then
            hs.execute("defaults write com.apple.FinalCut timeFormatLK -data " .. value)
            for i, v in pairs(GeneralPanel.mt.TIME_DISPLAY) do
                if value == v then
                    return i
                end
            end
        else
            log.ef("Invalid value for Time Display")
            return nil
        end
    else
        --------------------------------------------------------------------------------
        -- Getter:
        --------------------------------------------------------------------------------
        local output, executeStatus = hs.execute("defaults read com.apple.FinalCut timeFormatLK")
        if executeStatus and output then
            local length = string.len(output)
            if string.sub(output, 1, 1) == "<" and string.sub(output, length -1, length - 1 ) == ">" then
                local data = string.sub(output, 2, length - 2)
                data = string.gsub(data, " ", "")
                for i, v in pairs(GeneralPanel.mt.TIME_DISPLAY) do
                    if data == v then
                        return i
                    end
                end
            end
        end
        return nil
    end
end

--- cp.apple.finalcutpro.prefs.GeneralPanel(preferencesDialog) -> GeneralPanel
--- Constructor
--- Creates a new `GeneralPanel` instance.
---
--- Parameters:
---  * parent - The parent object.
---
--- Returns:
---  * A new `GeneralPanel` object.
function GeneralPanel:initialize(preferencesDialog)
    Panel.initialize(self, preferencesDialog, "PEGeneralPreferenceName")
end

--- cp.apple.finalcutpro.presfs.GeneralPanel.timeDisplay <cp.ui.PopUpButton>
--- Field
--- The "Time Display" `PopUpButton`.
function GeneralPanel.lazy.value:timeDisplay()
    return PopUpButton(self, self.UI:mutate(function(original)
        return childFromTop(original(), 1, PopUpButton.matches)
    end))
end

--- cp.apple.finalcutpro.presfs.GeneralPanel.resetDialogWarnings <cp.ui.Buton>
--- Field
--- The "Reset Dialog warnings" `Button`.
function GeneralPanel.lazy.value:resetDialogWarnings()
